var ivStr="16-Bytes--String";
var aesEncrypt=function(r,t){
	var e=CryptoJS.enc.Utf8.parse(r);
	var p=CryptoJS.enc.Utf8.parse(t);
	var a=CryptoJS.enc.Utf8.parse(ivStr);
	var n=CryptoJS.AES.encrypt(e,p,{iv:a,mode:CryptoJS.mode.CBC,padding:CryptoJS.pad.Pkcs7});
	return CryptoJS.enc.Base64.stringify(n.ciphertext)
};
var aesDecrypt=function(r,t){
	var e=CryptoJS.enc.Utf8.parse(t);
	var p=CryptoJS.enc.Utf8.parse(ivStr);
	var a=CryptoJS.AES.decrypt(r,e,{iv:p,mode:CryptoJS.mode.CBC,padding:CryptoJS.pad.Pkcs7});
	var n=a.toString(CryptoJS.enc.Utf8);
	return n
};